var resize_timeout;function getMarginAboutContainer(){return($(window).width()-$(".about__container").outerWidth())/2}function setCarouselSettings(){$(".owl-carousel").owlCarousel({items:3,nav:!0,dots:!0,margin:35,navContainer:".owl-nav",dotsContainer:".owl-dots",responsiveClass:!0,responsive:{280:{items:1,stagePadding:16,center:!0,mergeFit:!0},768:{items:2,stagePadding:24,mergeFit:!1},769:{items:3,stagePadding:getMarginAboutContainer()}}})}$(window).on("load",()=>{setCarouselSettings()}),$(window).on("resize orientationchange",function(){clearTimeout(resize_timeout),resize_timeout=setTimeout(function(){$(window).trigger("resized")},250)}),$(window).on("resized",()=>{$(window).width()>769&&$(".owl-stage").css({"padding-left":`${getMarginAboutContainer()}px`,"padding-right":`${getMarginAboutContainer()}px`})});